import { Component, OnInit } from '@angular/core';
import { PokeApiService } from 'src/app/service/poke-api.service';

@Component({
  selector: 'poke-list',
  templateUrl: './poke-list.component.html',
  styleUrls: ['./poke-list.component.scss']
})
export class PokeListComponent implements OnInit {

  public getAllPokemons: any;

  constructor( private pokeService: PokeApiService) { }

  ngOnInit() {
    this.pokeService.apiListAllPokemons.subscribe(
      res => {
        this.getAllPokemons= res.results;
        console.log(this.getAllPokemons)
      }
    )
  }
}
<ng-template ngFor let-pokemon [ngForOf]="getAllPokemons">
  <a class="pokemon">
    <section class="pokemon_status">
        <h2>{{pokemon.name}}</h2>
        <ul>
            <li>{{pokemon.status.types[0].type.name}}</li>
            <li>{{pokemon.status.types[1].type.name}}</li>
        </ul>
    </section>

    <section class="pokemon_image">
        <img src="../../../assets/pokemon.svg" alt="">
    </section>
</a>

</ng-template>
